jQuery(document).ready(function() {
    var videoArray = [];
    jQuery(function() {
        jQuery('.inv_playerContainer').each(function() {
            var vID = jQuery(this).attr("id");
            videoArray.push(vID);
            jQuery(this).hide();
        });
        jQuery.each(videoArray, function(index){
          jQuery("#"+ videoArray[index]).InvPlayer();
        });
    });
    
      jQuery("a.open-popup-link,a.open-popup-link-fw").magnificPopup({
          type: 'inline',
          midClick: true,
          removalDelay: 500,
          mainClass: 'mfp-fade',
          callbacks: {
              open: function() {
                  jQuery(this.content).find(".inv_playerContainer").InvPlayer().done(function(e) {
                        var f = e;
                        if(jQuery(".signpost-video").hasClass("s-video-autoplay")){
                            setTimeout(function(){
                                  f.playVideo();
                             },2000);
                        }
                        // setTimeout(function(){
                        //   f.playVideo();
                        // },2000);
                        jQuery('.mfp-container').addClass('full-width-video-popup').css('overflow', 'auto');
                        jQuery('.full-width-video-popup .white-popup .inv_playerContainer').css('display', 'block');
                  });
              },
              close: function() {
                  jQuery(this.content).find(".inv_playerContainer").InvPlayer().done(function(e) {
                      e.stopVideo();
                      jQuery('body').removeClass('noScroll');
                  });
                  jQuery(".s-video-lightbox").removeClass("hide-bgimg");
              },
              buildControls: function() {
               //this.contentContainer.append(this.arrowLeft.add(this.arrowRight));
              }
          }
        });
    

    jQuery(".signpost-video .more-link a, .signpost-video .open-in-place").on("click",spVideoStart);
    jQuery(".video-close").on("click",spVideoStop);
    jQuery(".signpost-video a.play-link").on("click",spFullWidthCboxVideoStart);
    jQuery(".signpost-video span.play-link").on("click",spFullWidthInplaceVideoStart);
    jQuery(".mfp-close").on("click",spFullWidthVideoStop);

    function spVideoStart(e) {
        e.stopPropagation();
        //console.log("start");
        jQuery(this).closest(".signpost").find(".inv_playerContainer").show().InvPlayer().done(function(e) {
            e.playVideo();
        });
        jQuery(this).closest(".signpost").find('.more-link a').addClass("videoPlaying");
        jQuery(this).closest(".signpost").addClass("hide-bgimg");
        jQuery(this).closest(".signpost").find(".video-close").show();
        jQuery(this).closest(".signpost").find(".signpost-content, .video-link").hide();
        jQuery(this).parents().find(".open-in-place .signpost-placeholder-t2, .signpost-video .play-link").hide();
    }
    function spVideoStop(e) { 
        e.stopPropagation();
        jQuery(this).siblings(".inv_playerContainer").InvPlayer().done(function(e) {
            e.stopVideo();
        });
        jQuery(this).closest(".signpost").find('.more-link a').removeClass("videoPlaying");
        jQuery(this).siblings(".inv_playerContainer").hide();
        jQuery(this).closest(".signpost").removeClass("hide-bgimg");
        jQuery(this).closest(".signpost").find(".video-close").hide();
        jQuery(this).closest(".signpost").find(".signpost-content, .video-link").show();
        jQuery(this).parents().find(".open-in-place .signpost-placeholder-t2, .signpost-video .play-link").show();
    }
   /* function spFullWidthInplaceVideoStart() {
       jQuery(jQuery(this)).parent().siblings(".inv_playerContainer").show().InvPlayer().done(function(e) {
            e.playVideo();
        });
        jQuery(this).parent().closest(".signpost").find('.more-link a').addClass("videoPlaying");
        jQuery(this).parent().closest(".signpost").addClass("hide-bgimg");
        jQuery(this).closest(".signpost").find(".video-close").show();
        jQuery(this).parent().closest(".signpost").find(".signpost-content, .video-link").hide();
        jQuery(this).parents().find(".open-in-place .signpost-placeholder-t2, .signpost-video .play-link").hide();
    }*/

    function spFullWidthInplaceVideoStart(e) {
        e.stopPropagation();
        var selector;
        if(jQuery(jQuery(this)).parent().siblings(".inv_playerContainer").length > 0) {
            selector = jQuery(jQuery(this)).parent();
            jQuery(this).parent().closest(".signpost").find(".signpost-placeholder-t2").show();
            jQuery(this).parent().closest(".signpost").find(".signpost-content, .video-link,.play-link").hide();
            selector.siblings(".inv_playerContainer").show().InvPlayer().done(function(e) {
                e.playVideo();
            });
        } else {
            selector = jQuery(this).closest(".signpost-video");
            jQuery(this).closest(".signpost").find(".signpost-content, .video-link,.play-link").show();
            jQuery(this).closest(".signpost").find(".signpost-placeholder-t2").hide();
            selector.find(".inv_playerContainer").show().InvPlayer().done(function(e) {
                e.playVideo();
            });
        }
        selector.find(".video-close").show();
        selector.addClass("hide-bgimg");
        selector.find('.more-link a').addClass("videoPlaying");
    }
    
    function spFullWidthCboxVideoStart(e) {
        e.stopPropagation();
        jQuery(jQuery(this).attr("href")).closest(".inv_playerContainer").show().InvPlayer().done(function(e) {
            e.playVideo();
        });
        jQuery(this).closest(".signpost").find('.more-link a').addClass("videoPlaying");
        jQuery(this).closest(".signpost:not(.signpost-bg-img)").addClass("hide-bgimg");
        //jQuery(this).closest(".signpost").find(".signpost-content, .video-link").hide();
        //jQuery(this).parents().find(".open-in-place .signpost-placeholder-t2, .signpost-video .play-link").hide();
    }
    function spFullWidthVideoStop(e) { 
        e.stopPropagation();
        jQuery(jQuery(this).attr("href")).closest(".inv_playerContainer").InvPlayer().done(function(e) {
            e.stopVideo();
        });
        jQuery(".mfp-container ").trigger("click");
        jQuery(this).closest(".signpost").find('.more-link a').removeClass("videoPlaying");
        jQuery(this).siblings(".inv_playerContainer").hide();
        jQuery(this).closest(".signpost").removeClass("hide-bgimg");
        jQuery(this).closest(".signpost").find(".video-close").hide();
        jQuery(this).closest(".signpost").find(".signpost-content, .video-link").show();
        jQuery(this).parents().find(".open-in-place .signpost-placeholder-t2, .signpost-video .play-link").show();
    }
    
    //fullColVideo
    jQuery(".signpost-video").each(function(){
        jQuery(this).find(".signpost-wrap").css("height", (jQuery(this).find(".signpost-wrap").width()/16)*9);
    });
  //jQuery(".signpost-video .signpost-wrap").css("height", (jQuery(".signpost-video .signpost-wrap").width()/16)*9);
});
jQuery(window).resize(function(){
    setTimeout(function(){
        jQuery(".signpost-video").each(function(){
            jQuery(this).find(".signpost-wrap").css("height", (jQuery(this).find(".signpost-wrap").width()/16)*9);
        });
    },10);
    
}); 
