jQuery(document).ready(function($){

  if($(".social-share-icon-wrap .swiper-slide").length<6){
    $(".social-share-icon-wrap").addClass("button-hide");
    $(".swiper-button-prev").addClass("button-hide");
    $(".swiper-button-next").addClass("button-hide");
  }
  if ($(window).width() < 992) {
    $(".swiper-container.t2-social-share").addClass("disabled");
  }

  /*top nav share JS starts*/
  $(document).on("click", ".top-nav-share > a", function(){
    if($(".top-nav-search").hasClass("active")){
      closePopup();
      $(".top-nav-search").removeClass("active");
      $(".body-overlay").removeClass("on");
      $(".social-share-overlay").removeClass("on");
      //reactivate popup
      $(this).parent().toggleClass("active");
      $(".body-overlay").toggleClass("on");
      $(".social-share-overlay").toggleClass("on");
      $("body").addClass("overflow-fixed");
      // social share slider
      /*swiper JS start*/
      var share_swiper = new Swiper('.swiper-container.t2-social-share', {
        slidesPerView: 5,
        autoplay:false,
        loop:false,
        spaceBetween:0,
        // init: false,
        navigation: {
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev',
        },
        normalizeSlideIndex:false,

      });
    }
    else if($(".top-nav-share").hasClass("active")){
      closePopup();
      $("body").removeClass("overflow-fixed");
      $(".top-nav-share").removeClass("active");
      $(".body-overlay").removeClass("on");
      $(".social-share-overlay").removeClass("on");
    }else{
      $("body").addClass("overflow-fixed");
      $(this).parent().toggleClass("active");
      $(".body-overlay").toggleClass("on");
      $(".social-share-overlay").toggleClass("on");
      //update social share slider
      var share_swiper = new Swiper('.swiper-container.t2-social-share', {
        slidesPerView: 5,
        autoplay:false,
        loop:false,
        spaceBetween:0,
        // init: false,
        navigation: {
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev',
        },
        normalizeSlideIndex:false,

      });
    }


  });
  $(document).on("click", ".top-nav-share .social-header .bar-close", function(){
    if($(".top-nav-share").hasClass("active")){
      $(".top-nav-share").toggleClass("active");
      $(".body-overlay").toggleClass("on");
      $(".social-share-overlay").toggleClass("on");
      $("body").removeClass("overflow-fixed");
    }
  });
  /*top nav search JS ends*/
  function closePopup(){
    $("body").removeClass("overflow-fixed");
    $(".top-nav-search").removeClass("active");
    $(".body-overlay").removeClass("on");
    $(".social-share-overlay").removeClass("on");
  }
  $(document).keyup(function(event) {
    //enable keyboard buttons for prev and next
    keyCode(event);

  });
  /*keyboard key functionality*/
  function keyCode(event) {
    var x = event.keyCode;
    if(event.keyCode == 37) {
      $(".t2-share.active .t2-share-bar .swiper-button-prev").trigger('click');
    }
    //esc key
    if(event.keyCode == 39) {
      $(".t2-share.active .t2-share-bar .swiper-button-next").trigger('click');
    }
    if(event.keyCode == 27) {
      closePopup();
      $(".top-nav-share").removeClass('active');
    }
  }


  /*accordian functionality on mobile*/
  if ($(window).width() < 767) {
    if($("div.social-header").hasClass("link-added")){
      return;
    }else{
      $("div.social-header").addClass("link-added");
      $( '<a href="javascript:void(0)"><span class="sr-only">share</span></a>' ).prependTo("div.social-header");
    }
  }
  $(document).on("click", ".t2-share .social-header a", function(){
    if ($(window).width() < 768) {
      if($(".t2-share").hasClass("active")){
        $(".t2-share").removeClass("active");
        $(".t2-social-share").slideUp("fast");
      }else{
        $(".t2-share").addClass("active");
        $(".t2-social-share").slideDown("fast");
        $('nav.navbar.navbar-we-mega-menu').animate({scrollTop: '+=120px'}, 600);
      }
    }
  });


  /*top nav share JS ends*/

  $(window).resize(function(){

    if ($(window).width() < 992) {
      closePopup();
      $(".swiper-container.t2-social-share").addClass("disabled");

    }
    else{
      $(".swiper-container.t2-social-share").removeClass("disabled");
    }
    if ($(window).width() < 768) {
      $(".top-nav-share").removeClass("active");
      $(".t2-social-share").slideUp("fast");
      //append anchor for share accordian functionality
      if($("div.social-header").hasClass("link-added")){
        return;
      }else{
        $("div.social-header").addClass("link-added");
        $( '<a href="javascript:void(0)"><span class="sr-only">share</span></a>' ).prependTo("div.social-header");
      }
    }

  });


});
